    public function index(Request $request, $leadid=0){
        $items = Invoice::where('is_delete', '=', '0')->orderBy('created_at', 'desc')->get();
        $customers = Customer::where('is_delete', '=', '0')->orderBy('created_at', 'desc')->get();
        $products = Product::where('is_delete', '=', '0')->orderBy('created_at', 'desc')->get();
        $ac_taxslabs = Ac_taxslab::where('is_delete', '=', '0')->orderBy('created_at', 'desc')->get();
        $company_banks = Company_bank::where('is_delete', '=', '0')->orderBy('created_at', 'desc')->get();
        $invoice_id = $this->invoicegenerate_refkey(1,'invoices','INVOICE');
        $product_id = $this->productgenerate_refkey(1,'product','P');
        $cdate = date('d-m-Y');
        if($request->leadid){
            $islead = $request->leadid;
        } else {
            $islead = 0;
        };
        //dd($islead);
        return view('admin.sales.crm-invoice',compact('items','invoice_id','product_id','cdate','customers','products','ac_taxslabs','islead','company_banks'));
    }

    public function productDataAjax(Request $request){
        $data = [];
        if($request->has('q')){
            $search = $request->q;
            /*$data = DB::table("view_leads")->select("id","name")->where('name','LIKE',"%$search%")->get();*/
            $data = Product::where('is_delete', '=', '0')->select("id","name")->where('name','LIKE',"%$search%")->get();
        }else{
            $data = Product::where('is_delete', '=', '0')->select("id","name")->get();
        }
        return response()->json($data);
    }

    public function manageInvoice(){
        $items = Invoice::where('is_delete', '=', '0')->orderBy('created_at', 'desc')->simplePaginate(20);
        return view('admin.sales.crm-invoice_list',compact('items'));
    }

    public function getLeadInvoice(){
        $items = View_lead::where('is_delete', '=', '0')->where('next_activity_id', '=', '2')->orderBy('created_at', 'desc')->simplePaginate(20);
        return view('admin.sales.crm-invoicelead',compact('items'));
    }

