    /**
     * Store a newly created resource in storage.
     *
     * @param  \Illuminate\Http\Request  $request
     * @return \Illuminate\Http\Response
     */
    public function store(Request $request){
        $items = new Invoice;
        $this->validate($request,[
            'name'=>'required',
            ]);
        $invoice_sid = $this->invoicegenerate_refkey(1,'invoices','INVOICE');
        $items->module = 1;
        $items->lead_id = $request->lead_id;
        $items->invoice_type = $request->invoice_type;
        $items->invoice_sn = $invoice_sid;
        $items->name = 'Invoice-'.date('dmYhis');
        $items->invoice_date = new DateTime("now");
        $items->description = $request->description;
        $items->due_date = $request->due_date;
        $items->tax_id = $request->tax_id;
        $items->total_tax = $request->total_tax;
        $items->total_discount = $request->total_discount;
        $items->total_shipping = $request->total_shipping;
        $items->total_sub_amount = $request->total_sub_amount;
        $items->total_amount = $request->total_amount;
        $items->payment_mode = $request->payment_mode;
        $items->payment_description = $request->payment_description;
        $items->customer_id = $request->customer_id;
        $items->customer_name = $request->customer_name;
        /*$items->company_id = $request->company_id;*/
        $is_company = $request->company_id;
        if ($is_company) {
            $items->company_id = $request->company_id;
        }else{
            $comitem = new Companie;
            $this->validate($request,[
                'company_id'=>'required',
                'contact_person_mobile'=>'required',
            ]);
            $comitem->name = $request->namecompany;
            $comitem->email = $request->email;
            $comitem->mobile = $request->mobile;
            $comitem->fax = $request->fax;
            $comitem->gst_no = $request->gst_no;
            $comitem->website = $request->website;
            $comitem->address  = $request->address;
            $comitem->city     = $request->city;
            $comitem->district = $request->district;
            $comitem->state    = $request->state;
            $comitem->country  = $request->country;
            $comitem->zipcode  = $request->zipcode;
            $comitem->contact_person = $request->contact_person;
            $comitem->contact_person_email = $request->contact_person_email;
            $comitem->contact_person_mobile = $request->contact_person_mobile;
            $comitem->contact_person_role = $request->contact_person_role;
            $comitem->description = 'Create Company Infomation';
            $comitem->created_by = Auth::user()->id;
            $comitem->save();
            $items->company_id = $comitem->id;
        };
        $items->company_name = $request->namecompany;
        $items->email = $request->email;
        $items->mobile = $request->mobile;
        $items->fax = $request->fax;
        $items->gst_no = $request->gst_no;
        $items->website = $request->website;
        $address_data = [
            'address'  => $request->address,
            'city'     => $request->city,
            'district' => $request->district,
            'state'    => $request->state,
            'country'  => $request->country,
            'zipcode'  => $request->zipcode
        ];
        $data = json_encode($address_data, true);
        /*json_decode($address_data, true);*/
        $items->address = $data;
        /*$items->address = 'Address: '.$request->address.', City: '.$request->city.', District: '.$request->district.', State: '.$request->state.', Country: '.$request->country.', ZIP Code: '.$request->zipcode;*/
        $items->contact_person = $request->contact_person;
        $items->contact_person_role = $request->contact_person_role;
        $items->contact_person_mobile = $request->contact_person_mobile;
        $items->contact_person_email = $request->contact_person_email;
        $items->bank_name = $request->bank_name;
        $items->bank_username = $request->bank_username;
        $items->bank_ifsc = $request->bank_ifsc;
        $items->bank_ac = $request->bank_ac;
        $items->bank_branch = $request->bank_branch;
        $items->status = $request->status;
        $items->created_by = Auth::user()->id;
        $items->save();
        /*if (true) {*/
        $id = $items->id;
        if ($id) {
            $id = $items->id;
            foreach ($request->product_name as $key=>$invitem){
                $invitem = new Invoice_item;
                $invitem->invoice_id = $id;
                $invitem->product_id = $request->product_id[$key];
                $invitem->name = $request->product_name[$key];
                $invitem->quantity = '1';
                $invitem->unit_price = $request->unit_price[$key];
                $invitem->tax_id = 6;
                $invitem->tax = $request->tax[$key];
                $invitem->taxamount = $request->taxamount[$key];
                $invitem->subtotal = $request->subtotal[$key];
                $invitem->created_by = Auth::user()->id;
                //dd($invitem);
                //echo "value[".$key."]: id[".$id."]".$request->product_id[$key]."<br>";
                $invitem->save();
            }
        }else{
            //dd('else');
        };
        session()->flash('message','Invoice Created Successfully');
        return back();
        /*return redirect('/inventory-invoice-list');*/
    }



    public function storeProduct(Request $request){
        //dd('Store');
        $items = new Product;
        $this->validate($request,[
            'name'=>'required',
            ]);
        $items->code = $this->productgenerate_refkey(1,'product','P');
        $items->name = $request->name;
        $items->image = $request->image;
        $items->category_id = $request->category_id;
        $items->subcategory_id = $request->subcategory_id;
        $items->brand = $request->brand;
        $items->service_tax = $request->service_tax;
        $items->cost_price = $request->cost_price;
        $items->sale_price = $request->sale_price;
        $items->stock_units = $request->stock_units;
        $items->description = $request->description;
        $items->created_by = Auth::user()->id;
        $items->save();
        return redirect('/crm-invoice');
    }

